import React, { useState } from "react";
import parse from "html-react-parser";

const Document = () => {
  const [value, setValue] = useState("");
  const [message, setMessage] = useState("");
  const [parsedContent, setParsedContent] = useState(null);

  const getMessages = async () => {
    const options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        message: value,
      }),
    };
    try {
      showLoader();
      const response = await fetch("http://localhost:8001/getDocuments", options);
      const data = await response.json();
      const ans = data.choices[0].message.content;
      const parsedHTML = parse(ans);
      setParsedContent(parsedHTML);

      var showdown = require('showdown'),
      converter = new showdown.Converter(),
      html = converter.makeHtml(ans);

      if (html) {
        hideloader();
      }
      setMessage(data.choices[0].message);
    } catch (error) {
      console.error(error);
    }
  };

  function hideLoader() {
    document.getElementById("loading").style.visibility = "hidden";
  }

  function showLoader() {
    document.getElementById("loading").style.visibility = "visible";
  }

  return (
    <div className="mainDiv">
      <style>
        {`
          img {
            max-width: 100%;
            max-height: 100%;
          }
          .picture1 {
            width: 200px;
            height: 300px;
          }
        `}
      </style>
      <div className="picture1">
        <img
          src="https://thumbs.dreamstime.com/b/constitutional-law-outline-vector-icon-thin-line-black-flat-simple-element-illustration-editable-justice-concept-isolated-175200290.jpg"
          alt=""
          srcSet=""
        />
      </div>
      <h1>Documents</h1>
      {parsedContent && <div>{parsedContent}</div>}
      <div className="input-container">
        <input value={value} onChange={(e) => setValue(e.target.value)} />
        <div id="submitDoc" onClick={getMessages}>
          &#10146;
        </div>
        {message && <p>{message.content}</p>}
      </div>
    </div>
  );
};

export default Document;
